quot;; case 7:return &quot;Q&quot;; case 8:return &quot;B&quot;; case 9:return &quot;J&quot;; case 0:return &quot;L&quot;; } }else if(ord($s)&amp;gt;=65 and ord($s)&amp;lt;=90){ //大写英文开头 return substr($s,0,1); }else if(ord($s)&amp;gt;=97 and ord($s)&amp;lt;=122){ //小写英文开头 return strtoupper(substr($s,0,1)); } else { return iconv_substr($s0,0,1,'utf-8');//中英混合的词语，不适合上面的各种情况，因此直接提取首个字符即可 } }

